    private void apiCallGetNewProject() {
        LoadingDialog.getInstance().show(this);

        Map<String, String> mapParams = new HashMap<>();
        try {
            ServiceManager serviceManager = new ServiceManager();
            serviceManager.apiCaller(EndPoints.kGetProjects, mapParams, NewProjectActivity.this, new APIResponse() {
                @Override
                public void onSuccess(JSONObject response) {
                    Log.d("API", "Success API ==== " + response.toString());

                    try {
                        JSONArray arrData = response.getJSONArray("data");

                        for (int i = 0; i < arrData.length(); i++) {
                            JSONObject dictNewProject = arrData.getJSONObject(i);
                            FragmentProjectListModel projectListModel = new FragmentProjectListModel();
                            projectListModel.setProjectID(dictNewProject.getInt("id"));
                            projectListModel.setProjectName(dictNewProject.getString("name"));
                            projectListModel.setProjectCast(dictNewProject.getInt("cost") + "");
                            projectListModel.setImgProjectLogoURL(dictNewProject.getString("image"));
                            arrNewProjects.add(projectListModel);
                        }
                        displayData();
                        LoadingDialog.getInstance().dismiss();
                    } catch (JSONException e) {
                        LoadingDialog.getInstance().dismiss();
                        throw new RuntimeException(e);
                    }
                }

                @Override
                public void onError(String error) {
                    LoadingDialog.getInstance().dismiss();
                }

                @Override
                public void onStart() {
                    Log.d("API", "Started Calling API");
                }
            });
        } catch (Exception e) {
            e.printStackTrace();
        }

    }

    private void displayData() {
        LinearLayoutManager layoutManager = new LinearLayoutManager(this);
        newProjectAdapter = new NewProjectAdapter(this, arrNewProjects);
        newProjectList.setLayoutManager(layoutManager);
        newProjectList.setAdapter(newProjectAdapter);
    }

